// Accedemos al arreglo de ideas
const input = items[0].json.output.scraped_summary;

// Unimos todas las ideas en un solo string con formato de lista numerada
const ideasText = input.map(entry => `${entry.id}.${entry.idea}`).join('\n\n');

// Devolvemos el string como único output
return [
  {
    json: {
      ideas_text: ideasText
    }
  }
];

// Accedemos al array de slides

const slides = items[0].json.message.content.slides;

// Unimos todos los textos de los slides en un solo string
const combinedText = slides.map(slide => `${slide.slide}. ${slide.text}`).join('\n\n');

// Devolvemos el resultado como un solo campo
return [
  {
    json: {
      combined_slides_text: combinedText
    }
  }
];

const result = items.map(item => {
  return `{ "media_fbid": "${item.json.media_fbid}" }`;
});

return [
  {
    json: {
      output: result.join(", ")
    }
  }
];

return items.map(item => {
  return {
    json: {
      media_fbid: item.json.id
    }
  };
});

return items.map(item => {
  return {
    json: {
      children: item.json.id
    }
  };
});

const result = items.map(item => {
  return `"${item.json.children}"`;
});

return [
  {
    json: {
      output: result.join(", ")
    }
  }
];

