const items = $input.all();

const updatedItems = items.map(item => {
  item.json.output = item.json.output
    // Quita cualquier salto de línea (uno o varios seguidos)
    .replace(/\n+/g, '')
    // Quita todas las comillas dobles
    .replace(/"/g, '');

  return item;
});

return updatedItems;

