const data = $('Analiza.Rss').first().json.output;

const post = `
**Resumen objetivo:**  
${data.resumen_objetivo}

**Puntos clave:**
${data.puntos_clave.map(p => `- ${p}`).join('\n')}

**Antecedentes esenciales:**  
${data.antecedentes}

**Posibles implicaciones:**  
${data.implicaciones}

**Términos técnicos clave:**
${data.terminos_tecnicos.map(t => `- **${t.termino}**: ${t.definicion}`).join('\n')}
`;

return [{ post }];
